<template>
  <div class="signup-container">
    <div class="signup-participators">
      <div class="signup-avator">
        <a
          v-for="(item, index) in infoData.recently_user"
          :key="item.user_id + index"
          :href="infoData.live_url + '/user/' + item.user_id"
          :title="item.user_name"
          :style="{ 'background-image': 'url(' + item.avatar_large + ')' }"
          class="signup-avator-item"
          target="link"
        ></a>
      </div>
      <div class="signup-text">已有{{ infoData.select_cnt }}名创作者参加</div>
    </div>
    <div class="signup-btn-group">
      <div class="pullup-button signup-button" v-if="isActivityEnd" @click="onPull"></div>
      <div class="signup-button" @click="onJoin" v-else></div>
      <router-link to="/info">
        <div class="signup-button-info"></div>
      </router-link>
    </div>
  </div>
</template>

<script lang="ts">
import { reactive, toRefs, watchEffect, computed } from "vue";
import { useStore } from "vuex";
import axios from "axios";
export default {
  setup() {
    const state = reactive({
      infoData: {
        'live_url': "",
        'recently_user': [],
        'select_cnt': 1218,
        'start_time': 0,
        'end_time': 0,
        'annual_user_info': null,
      },
    });
    axios
      .post("http://localhost:8081/info", {
        'annual_id': "2020",
        'app_id': 2608,
      })
      .then((res) => {
        if (res.data.code === 200) {
          state.infoData = res.data.data;
          state.infoData.recently_user.reverse();
        }
      });
    const store = useStore();
    const isActivityEnd = computed(() => store.state.isActivityEnd);
    watchEffect(() => {
      const infoData = state.infoData;
      if (infoData) {
        store.dispatch("setInfoData", state.infoData);
      }
    });
    const refState = toRefs(state);
    const onJoin = () => {
      const isLogin = store.state.isLogin;
      if (!isLogin) {
        location.href = "https://juejin.cn/login";
      } else {
        alert("开发中...");
      }
    };
    const onPull = () => {
      alert('开发中');
    }
    return {
      ...refState,
      onJoin,
      isActivityEnd,
      onPull
    };
  },
};
</script>

